mapper.select:
select 
	count(*)
from
	tbl_board_recommand
where
	board_type = #{type}
	and board_seq = #{seq}
	and recommand_user = #{user}

select 
	count(*)
from
	tbl_reply_empathy
where
	reply_seq = #{reply_seq}
	and recommand_user = #{recommand_user}
	and board_type = #{board_type}

mapper.delete:
delete tbl_board_reply
	where
		board_type = #{type}
		and board_seq = #{seq}

	delete tbl_board_recommand
		where
			board_type = #{type}
			and board_seq = #{seq}
